    def test_geometry(self):
        sgpd_geoseries = sgpd.GeoSeries([Point(0, 0), Point(1, 1)])
        assert isinstance(sgpd_geoseries.geometry, sgpd.GeoSeries)
        assert_series_equal(
            sgpd_geoseries.geometry.to_pandas(), sgpd_geoseries.to_pandas()
        )

